import React, {useState} from 'react';
import { Stack, HStack, VStack, FormLabel, FormControl, Input, Button } from '@chakra-ui/react';
import { InputGroup, InputRightElement } from '@chakra-ui/react'
import { useToast } from '@chakra-ui/react'

function Signup() {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [confirmpassword, setConfirmpassword] = useState("");
    const [password, setPassword] = useState("");
    const [pic, setPic] = useState();
    const [show,setShow] = useState(false);
    const [loading,setLoading] = useState(false);
    const toast = useToast();

    const handleClick = () => setShow(!show);
    const postDetails = (pics) => {
        setLoading(true);
        if(pic === undefined){
            toast({
                title: "Please Select an Image",
                status: "warning",
                duration: 5000,
                isClosable: true,
                position: "bottom",
            })
            return;
        }
        if(pics.type === "image/png" || pics.type === "image/jpeg"){
            const data = new FormData();
            data.append("file", pics);
            data.append("upload_preset", "mern-chat-app");
            data.append("cloud_name", "drlpmeub3");
            fetch("https://api.cloudinary.com/v1_1/drlpmeub3",{
                method: "post",
                body: data,
            }).then((res) => res.json())
            .then(data => {
                setPic(data.url.toString());
                console.log(data.url.toString());
                setLoading(false);
            })
            .catch((err)=>{
                console.log(err);
                setLoading(false);
            });
        } else{
            toast({
                title: "Please Select an Image",
                status: "warning",
                duration: 5000,
                isClosable: true,
                position: "bottom",
            });
            setLoading(false);
            return;
        }
    };
    const submitHandler = () => {

    }
  return (
    <VStack spacing = '5px'>
        <FormControl id = 'first-name' isRequired>
            <FormLabel>Name</FormLabel>
            <Input
                placeholder='Enter Your Name'
                onChange={(e)=>setName(e.target.value)}
            />
        </FormControl>
        <FormControl id = 'email' isRequired>
            <FormLabel>Email</FormLabel>
            <Input
                placeholder='Enter Your Email'
                onChange={(e)=>setEmail(e.target.value)}
            />
        </FormControl>
        <FormControl id = 'password' isRequired>
            <FormLabel>Password</FormLabel>
            <InputGroup>
                <Input
                    type = {show ? 'text':'password'}
                    placeholder='Enter Your Password'
                    onChange={(e)=>setPassword(e.target.value)}
                />
                <InputRightElement width = "4.5rem">
                    <Button h = "1.75rem" size = "sm" onClick = {handleClick}>
                        {show? "Hide" : "Show"}
                    </Button>
                </InputRightElement>
            </InputGroup>
        </FormControl>
        <FormControl id = 'password' isRequired>
            <FormLabel>Password</FormLabel>
            <InputGroup>
                <Input
                    type = {show ? 'text':'password'}
                    placeholder='Confirm Password'
                    onChange={(e) => setConfirmpassword(e.target.value)}
                />
                <InputRightElement width = "4.5rem">
                    <Button h = "1.75rem" size = "sm" onClick = {handleClick}>
                        {show? "Hide" : "Show"}
                    </Button>
                </InputRightElement>
            </InputGroup>
        </FormControl>
        <FormControl id = 'pic'>
            <FormLabel>Upload your Picture</FormLabel>
            <InputGroup>
                <Input
                    type = "file"
                    p = {1.5}
                    accept = "image/*"
                    onChange={(e) => postDetails(e.target.files[0])}
                />
                {/* <InputRightElement width = "4.5rem">
                    <Button h = "1.75rem" size = "sm" onClick = {handleClick}>
                        {show? "Hide" : "Show"}
                    </Button>
                </InputRightElement> */}
            </InputGroup>
        </FormControl>
        <Button
            colorScheme="blue"
            width="100%"
            style={{marginTop: 15}}
            onClick={submitHandler}
            loading = {loa}
        >
            Sign Up
        </Button>
    </VStack>
  )
}

export default Signup
